import { Component, OnInit, ViewChild, ElementRef, Renderer2 } from '@angular/core';
import { CommonService } from 'src/app/services/common.service';
import { HomeService } from 'src/app/services/home.service';

@Component({
  selector: 'app-total-counts',
  templateUrl: './total-counts.component.html',
  styleUrls: ['./total-counts.component.css']
})
export class TotalCountsComponent implements OnInit {
  totalcounts: any;

  @ViewChild('countElement', { static: true }) countElement: ElementRef;

  constructor(private homeService: HomeService, private commonService: CommonService, private renderer: Renderer2) { }

  ngOnInit(): void {
    this.homeService.getTotalCounts().subscribe({
      next: (res) => {
        this.totalcounts = res.data;
        this.updateCountingAnimation();
      },
      error: (err) => {
        console.log(err);
      }
    });
  }

  updateCountingAnimation(): void {
    if (this.totalcounts && this.countElement) {
      const initialCount = parseInt(this.countElement.nativeElement.getAttribute('data-to'), 10);
      const speed = parseInt(this.countElement.nativeElement.getAttribute('data-speed'), 10);

      let count = 0;

      this.countElement.nativeElement.textContent = '0'; // Initialize to 0

      const updateCount = () => {
        count++;
        this.renderer.setProperty(this.countElement.nativeElement, 'textContent', count.toString());

        if (count < initialCount) {
          setTimeout(updateCount, speed);
        }
      };

      setTimeout(updateCount, speed);
    }
  }
}
