import { Component, OnInit } from '@angular/core';
import { CommonService } from 'src/app/services/common.service';
import { HomeService } from 'src/app/services/home.service';

@Component({
  selector: 'app-works',
  templateUrl: './works.component.html',
  styleUrls: ['./works.component.css']
})
export class WorksComponent implements OnInit {

  works: any
  constructor(private homeService: HomeService,private commonService:CommonService) { }
  ngOnInit(): void {

    this.homeService.getWorks().subscribe({
      next: (res) => {

        this.works = res.data



      }, error: (err) => {
        console.log(err)
      }
    })

  }
  getImagePath(image:any){


    return this.commonService.createImagePath(image.data.attributes.url)
  }

}


<div class="works-about-area overflow-hidden">
    <div class="container">
    <div class="works-about-items default-padding">
      <div class="row align-center">
          <div *ngFor="let item of works"class="col-lg-6 info">
              <h5>Works About</h5>
              <h2>Trusted by 5,000+<br>Happy Customers</h2>
              <p>
                  {{item.attributes.Description}}</p>
              <ul>
                                              <li>
                      <h5>100% Client Satisfaction</h5>
                  </li>
                                              <li>
                      <h5>World Class Worker</h5>
                  </li>
                                          </ul>
                                      <a href="#" class="btn btn-theme effect btn-sm">Talk to a consultant</a>
                                   </div>
          <div  *ngFor="let item of works" class="col-lg-6">
              <div class="thumb">
                  <img decoding="async" [src]="getImagePath(item.attributes.Wpic)" alt="Thumb">
                  <div class="fun-fact">
                      <div class="timer" data-to="875" data-speed="5000">875</div>
                      <span class="medium">Completed Projects</span>
                  </div>
              </div>
          </div>
      </div>
    </div>
    </div>
    </div>
