import { Component, OnInit } from '@angular/core';
import { CommonService } from 'src/app/services/common.service';
import { HomeService } from 'src/app/services/home.service';

@Component({
  selector: 'app-about-us',
  templateUrl: './about-us.component.html',
  styleUrls: ['./about-us.component.css']
})
export class AboutUsComponent implements OnInit {

  aboutus: any
  constructor(private homeService: HomeService,private commonService:CommonService) { }
  ngOnInit(): void {

    this.homeService.getAboutUs().subscribe({
      next: (res) => {

        this.aboutus= res.data
       console.log("About" , this.aboutus)


      }, error: (err) => {
        console.log(err)
      }
    })

  }}

<div class="about-area relative reverse">
    <div class="container">
    <div class="row align-center">

      <div *ngFor="let item of aboutus" class="col-lg-6 info">
          <h5>About Us</h5>
          <h2>We Help IT Companies Scale Engineering Capacity</h2>
          <p>{{item.attributes.WhoWeAre}} </p>
          <div class="skill-items">
              <!-- Progress Bar Start -->


              <!-- <div class="progress-box">
                  <h5>Software Development</h5>
                  <div class="progress">
                      <div class="progress-bar" role="progressbar" data-width="88">
                           <span>88%</span>
                      </div>
                  </div>
              </div>


              <div class="progress-box">
                  <h5>Cyber Security</h5>
                  <div class="progress">
                      <div class="progress-bar" role="progressbar" data-width="95">
                           <span>95%</span>
                      </div>
                  </div>
              </div>


              <div class="progress-box">
                  <h5>Artificial Intelligence</h5>
                  <div class="progress">
                      <div class="progress-bar" role="progressbar" data-width="80">
                           <span>80%</span>
                      </div>
                  </div>
              </div>
     -->
     <div *ngFor="let item of aboutus" class="col-lg-3 col-md-6 item">
        <div class="fun-fact">
          <div class="icon">
            <i class="fas fa-bars"></i>
          </div>
          <div class="info">
            <div class="timer" [attr.data-to]="item.attributes.Number" data-speed="5000" id="countElement">
              {{item.attributes.Number}}</div>
            <span class="medium" id="nameElement">{{item.attributes.Namess}}</span>
            <script>
              function startCounting() {
                const countElement = document.getElementById('countElement');
                const nameElement = document.getElementById('nameElement');
                const initialCount = parseInt(countElement.getAttribute('data-to'));
                const speed = parseInt(countElement.getAttribute('data-speed'));
                let count = 0;

                countElement.textContent = initialCount;


                nameElement.textContent = item.attributes.Namess;


                function updateCount() {
                  count++;
                  countElement.textContent = count;


                  if (count === initialCount) {
                    clearInterval(interval);
                  }
                }


                const interval = setInterval(updateCount, speed);
              }

              document.addEventListener('DOMContentLoaded', startCounting);
            </script>
          </div>
        </div>
      </div>
              <!-- End Progressbar -->

          </div>
      </div>

      <div class="col-lg-6 thumbs">
          <img decoding="async" src="assets/wp-content/uploads/2022/03/7-2.png" alt="Thumb">
      </div>

    </div>
    </div>
    </div>
