import { Component, OnInit, AfterViewInit, ViewChild, ElementRef } from '@angular/core';

// ...

export class TotalCountsComponent implements OnInit, AfterViewInit {
  totalcounts: any;

  @ViewChild('countElement') countElement: ElementRef;

  constructor(private homeService: HomeService, private commonService: CommonService) {}

  ngOnInit(): void {
    this.homeService.getTotalCounts().subscribe({
      next: (res) => {
        this.totalcounts = res.data;
      },
      error: (err) => {
        console.log(err);
      },
    });
  }

  ngAfterViewInit(): void {
    this.startCounting();
  }

  startCounting(): void {
    if (this.totalcounts && this.countElement) {
      const initialCount = parseInt(this.countElement.nativeElement.getAttribute('data-to'), 10);
      const speed = parseInt(this.countElement.nativeElement.getAttribute('data-speed'), 10);
      let count = 0;

      this.countElement.nativeElement.textContent = initialCount;

      function updateCount() {
        count++;
        this.countElement.nativeElement.textContent = count;

        if (count === initialCount) {
          clearInterval(interval);
        }
      }

      const interval = setInterval(updateCount.bind(this), speed);
    }
  }
}
